import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";


interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: string;
  store_phone_number: string;
  store_url: string;
  store_img: string;
  tags: string[]; // „Çø„Ç∞„ÅÆÈÖçÂàó„Å®„Åó„Å¶ÂÆöÁæ©
  reviews?: Review[];
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}



const DogRunDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [store, setStore] = useState<Store | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isFavorite, setIsFavorite] = useState<boolean>(false);
  const userId = 1; // ‰ªÆ„ÅÆ„É¶„Éº„Ç∂„ÉºID

  //MAP
  const MAP_API_KEY = "AIzaSyBamO2vaf6PMNHy3bhMJd_2FO2I6IUgEpE";

  useEffect(() => {
    const fetchStoreAndFavorite = async () => {
      try {
        //Â∫óËàó„Éá„Éº„ÇøÂèñÂæó
        const storeResponse = await fetch(`http://localhost:5003/stores/detail/${id}`);
        if (!storeResponse.ok) throw new Error("Â∫óËàóÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");

        const storeData: Store = await storeResponse.json();
        setStore(storeData);

        //„ÅäÊ∞ó„Å´ÂÖ•„ÇäÁä∂ÊÖã„ÇíÂèñÂæó
        const favoriteResponse = await fetch(`http://localhost:5003/favorites/${userId}`);

        const favoriteData: { store_id: number }[] = await favoriteResponse.json();
        setIsFavorite(favoriteData.some((fav) => fav.store_id === storeData.store_id));
      } catch (err: any) {
        console.error(err.message);
        setError("„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
      }
    };
    fetchStoreAndFavorite();
  }, [id]);

  // „ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆËøΩÂä†„ÉªËß£Èô§
  const handleFavoriteClick = async () => {
    try {
      const response = await fetch("http://localhost:5003/favorites", {
        method: isFavorite ? "DELETE" : "POST",
        headers: {
          "Content-Type": "application/json",// ÈÄÅ„Çã„Éá„Éº„Çø„ÅåJSONÂΩ¢Âºè„Å†„Å®‰ºù„Åà„Çã
        },
        body: JSON.stringify({
          user_id: userId, // userId„Çí‰ΩøÁî®
          store_id: store?.store_id, // ÂãïÁöÑ„Å´ÁèæÂú®„ÅÆstore_id„Çí‰ΩøÁî®
        }),
      })
        .then((response) => response.json())
        .then((data) => console.log("„ÅäÊ∞ó„Å´ÂÖ•„Çä„Éá„Éº„Çø:", data))
        .catch((error) => console.error("„Ç®„É©„Éº:", error));

      setIsFavorite(!isFavorite); // „ÅäÊ∞ó„Å´ÂÖ•„ÇäÁä∂ÊÖã„Çí„Éà„Ç∞„É´
    } catch (error) {
      console.log(userId);
      console.log(store);
      console.error("„ÅäÊ∞ó„Å´ÂÖ•„ÇäÊõ¥Êñ∞„Ç®„É©„Éº:", error);
      setError("„ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
    }
  };


  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await fetch(
          `http://localhost:5003/stores/detail/${id}`
        );
        if (!response.ok) {
          throw new Error(`„Çµ„Éº„Éê„Éº„Ç®„É©„Éº: ${response.status}`);
        }
        const data = await response.json();
        console.log(data);

        setStore(data);
      } catch (error) {
        console.error("Â∫óËàóÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:", error);
      }
    };
    fetchStores();
  }, [id]);

  // Â∫óËàó„Éá„Éº„Çø„Å®„É¨„Éì„É•„Éº„ÇíÂèñÂæó„Åó„Å¶Ë®≠ÂÆö„Åô„ÇãÈñ¢Êï∞

  useEffect(() => {
    const fetchStoreWithReviews = async () => {
      try {
        const storeResponse = await fetch(`http://localhost:5003/stores/detail/${id}`);
        const reviewResponse = await fetch(`http://localhost:5003/reviews`);

        if (!storeResponse.ok || !reviewResponse.ok) {
          throw new Error("„Éá„Éº„ÇøÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
        }

        const storeData: Store = await storeResponse.json();
        const reviewData: Review[] = await reviewResponse.json();

        // Â∫óËàó„Å´Èñ¢ÈÄ£‰ªò„Åë„Çâ„Çå„ÅüÂè£„Ç≥„Éü„ÇíÁµê„Å≥‰ªò„Åë„Çã
        const reviews = reviewData.filter((review) => review.store_id === storeData.store_id);
        setStore({ ...storeData, reviews });
      } catch (err: any) {
        console.error("„Éá„Éº„ÇøÂèñÂæó‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü:", err);
        setError("Â∫óËàóÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
      }
    };

    fetchStoreWithReviews();
  }, [id]);

  if (error) {
    return (
      <div style={{ padding: "20px", backgroundColor: "#FAF3E0", textAlign: "center" }}>
        <p>{error}</p>
      </div>
    );
  }

  // store„Åånull„ÅÆÂ†¥Âêà„ÅÆÂá¶ÁêÜ
  if (!store) {
    return (
      <div style={{ padding: "20px", backgroundColor: "#FAF3E0", textAlign: "center" }}>
        <p>„Éá„Éº„Çø„ÇíË™≠„ÅøËæº„Çì„Åß„ÅÑ„Åæ„Åô..üêï</p>
      </div>
    );
  }
  return (
    <div className="container">
    <h1 className="store-name">{store.store_name}</h1>
    <img src={store.store_img} alt={store.store_name} className="store-image" />

      {/* „ÅäÊ∞ó„Å´ÂÖ•„Çä„Éú„Çø„É≥ */}
      <button
        onClick={handleFavoriteClick}
        className={`favorite-button ${isFavorite ? "active" : "inactive"}`}
      >
        {isFavorite ? "„ÅäÊ∞ó„Å´ÂÖ•„Çä" : "„ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§"}
      </button>


      <div className="rating-container">
        <div>‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ</div>
        <div
          className="filled-stars"
          style={{ width: `${(averageRating / 5) * 100}%` }}
        >
          ‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ
        </div>
      </div>
      <p>{averageRating.toFixed(1)}</p>

      {/* Â∫óËàóÊÉÖÂ†± */}
      <p>
        <strong>‰ΩèÊâÄ: </strong>
        {store.store_address}
      </p>
      {/*/Google map Âüã„ÇÅËæº„Åø*/}
      <div style={{ margin: "20px 0" }}>
        <iframe
          title="Google Map"
          width="100%"
          height="300"
          style={{ border: "0", borderRadius: "8px" }}
          src={`https://www.google.com/maps/embed/v1/place?key=${MAP_API_KEY}&q=${encodeURIComponent(
            store.store_address
          )}`}
          allowFullScreen
        ></iframe>
      </div>
      <p>ÈõªË©±Áï™Âè∑: {store.store_phone_number}</p>
      <p>Âñ∂Ê•≠ÊôÇÈñì: {store.store_opening_hours}</p>
      {store.reviews && store.reviews.length > 0 ? (
        store.reviews.map((review) => (
          <div key={review.id}></div>
        ))
      ) : (
        <p>„Åæ„Å†Âè£„Ç≥„Éü„ÅØ„ÅÇ„Çä„Åæ„Åõ„Çì</p>
      )}
      {/*Âè£„Ç≥„Éü‰∏ÄË¶ß„Éö„Éº„Ç∏„Å∏„ÅÆ„É™„É≥„ÇØ„ÇíËøΩÂä†*/}
      {store.reviews && store.reviews.length > 0 && (
        <Link
          to={`/dogrun/reviews/${store.store_id}`}className="link">
          Âè£„Ç≥„Éü„ÇíË¶ã„Çã
        </Link>
      )}
      <br /><br />
      <a
        href={store.store_url}
      >
        Â∫óËàó„ÅÆÂÖ¨Âºè„Çµ„Ç§„Éà
      </a>
    </div>
  );
};

export default DogRunDetail;
