import React, { useEffect, useState } from "react";
import "../pages/FavoritePage.css";
import { Link } from "react-router-dom";
import Header from "./Header";
import Footer from "./Footer";

interface Favorite {
  id: number;
  user_id: number;
  store_id: number;
  store_name: string;
  store_address: string;
  store_URL: string;
  store_img: string;
  store_type: number;
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}

const FavoritePage: React.FC = () => {
  const [favorites, setFavorites] = useState<Favorite[]>([]);
  const [reviews, setReviews] = useState<Review[]>([]);

  useEffect(() => {
    const fetchFavorites = async () => {
      try {
        const response = await fetch("http://localhost:5003/favorites/1", {
          method: "GET",
          credentials: "include",
          headers: { "Content-Type": "application/json" },
        });

        if (!response.ok) {
          throw new Error("„Ç®„É©„Éº„ÅåËøî„Åï„Çå„Åæ„Åó„Åü");
        }

        const data: Favorite[] = await response.json();
        setFavorites(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFavorites();
  }, []);

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch("http://localhost:5003/reviews", {
          method: "GET",
          headers: { "Content-Type": "application/json" },
        });

        if (!response.ok) {
          throw new Error("Âè£„Ç≥„Éü„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
        }

        const data: Review[] = await response.json();
        setReviews(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchReviews();
  }, []);

 // üìå Â∫óËàó„ÅÆÁ®ÆÈ°û„Å´Âøú„Åò„Åü„Éö„Éº„Ç∏„ÅÆÈÅ∑ÁßªÂÖà„ÇíÂèñÂæó
 const getCategoryPath = (storeType: number, storeId: number) => {
  switch (storeType) {
    case 1:
      return `/dogrun/detail/${storeId}`;
    case 2:
      return `/dogcafe/detail/${storeId}`;
    case 3:
      return `/petshop/detail/${storeId}`;
    case 4:
      return `/hospital/detail/${storeId}`;
    default:
      return `/detail/${storeId}`;
  }
};
  return (
    <>
      <Header />
      <div className="favorite-container">
        <header className="app-header">
          <h1 className="title">„ÅäÊ∞ó„Å´ÂÖ•„Çä„É™„Çπ„Éà</h1>
        </header>

        <ul className="favorite-list">
          {favorites.length > 0 ? (
            favorites.map((favorite) => {
              // ÂØæË±°Â∫óËàó„ÅÆÂè£„Ç≥„Éü„Éá„Éº„Çø„ÇíÂèñÂæó
              const storeReviews = reviews.filter((review) => review.store_id === favorite.store_id);

              // Âπ≥ÂùáË©ï‰æ°„ÇíË®àÁÆóÔºàÂè£„Ç≥„Éü„Åå„ÅÇ„ÇãÂ†¥Âêà„ÅÆ„ÅøÔºâ
              const averageRating =
                storeReviews.length > 0
                  ? storeReviews.reduce((sum, review) => sum + review.rating, 0) / storeReviews.length
                  : 0;

              return (
                <Link to={`/dogrun/detail/${favorite.store_id}`} className="favorite-link" key={favorite.id}>
                  <li className="favorite-item">
                    {/* Â∫óËàóÁîªÂÉè */}
                    <img
                      src={favorite.store_img ? favorite.store_img : "http://via.placeholder.com/150"}
                      alt={favorite.store_name}
                      className="favorite-image"
                    />
                    <h2 className="favorite-title">{favorite.store_name}</h2>

                    {/* Âè£„Ç≥„Éü„ÅÆÂπ≥ÂùáË©ï‰æ°„ÇíË°®Á§∫ */}
                    <div className="review-average">
                      {[1, 2, 3, 4, 5].map((value) => (
                        <span
                          key={`star-${favorite.store_id}-${value}`}
                          className={`star ${value <= Math.round(averageRating) ? "selected" : ""}`}
                        >
                          ‚òÖ
                        </span>
                      ))}
                      <strong>{averageRating.toFixed(1)}</strong>
                    </div>
                  </li>
                </Link>
              );
            })
          ) : (
            <p className="no-favorites">„ÅäÊ∞ó„Å´ÂÖ•„Çä„Åå„Åæ„Å†ÁôªÈå≤„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ</p>
          )}
        </ul>
      </div>
      <Footer/>
    </>
  );
};

export default FavoritePage;
