import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./DogRunDetail.css";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: string;
  store_phone_number: string;
  store_url: string;
  store_img: string;
  tags: string[];
  reviews?: Review[];
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}

const DogRunDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [store, setStore] = useState<Store | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isFavorite, setIsFavorite] = useState<boolean>(false);
  const userId = 1;

  const MAP_API_KEY = "AIzaSyBamO2vaf6PMNHy3bhMJd_2FO2I6IUgEpE";

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const storeResponse = await fetch(
          `http://localhost:5003/stores/detail/${id}`
        );
        if (!storeResponse.ok) throw new Error("Â∫óËàóÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");

        const storeData: Store = await storeResponse.json();

        const favoriteResponse = await fetch(
          `http://localhost:5003/favorites/${userId}`
        );
        const favoriteData: { store_id: number }[] = await favoriteResponse.json();
        const isFavorited = favoriteData.some(
          (fav) => fav.store_id === storeData.store_id
        );

        setStore(storeData);
        setIsFavorite(isFavorited);
      } catch (err: any) {
        console.error(err.message);
        setError("„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
      }
    };
    fetchStoreData();
  }, [id, userId]);

  const handleFavoriteClick = async () => {
    if (!store) return;

    try {
      const method = isFavorite ? "DELETE" : "POST";
      const response = await fetch("http://localhost:5003/favorites", {
        method,
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user_id: userId,
          store_id: store.store_id,
        }),
      });

      if (!response.ok) throw new Error("„ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");

      setIsFavorite(!isFavorite);
    } catch (err) {
      console.error("„ÅäÊ∞ó„Å´ÂÖ•„ÇäÊõ¥Êñ∞„Ç®„É©„Éº:", err);
      setError("„ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
    }
  };


  const handleReviewSubmit = async (rating: number, comment: string) => {
    try {
      const response = await fetch("http://localhost:5003/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          store_id: store?.store_id, // Â∫óËàóID„ÇíÈÄÅ‰ø°
          rating, // Ë©ï‰æ°
          comment, // „Ç≥„É°„É≥„Éà
        }),
      });

      if (!response.ok) {
        throw new Error("Âè£„Ç≥„Éü„ÅÆÊäïÁ®ø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
      }

      const newReview = await response.json();
      setStore((prevStore) =>
        prevStore
          ? { ...prevStore, reviews: [...(prevStore.reviews || []), newReview] }
          : null
      );
    } catch (error) {
      console.error("Âè£„Ç≥„ÉüÊäïÁ®ø„Ç®„É©„Éº:", error);
      setError("Âè£„Ç≥„Éü„ÅÆÊäïÁ®ø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
    }
  };

  if (error) {
    return (
      <div className="container">
        <p>{error}</p>
      </div>
    );
  }

  if (!store) {
    return (
      <div className="container">
        <p>„Éá„Éº„Çø„ÇíË™≠„ÅøËæº„Çì„Åß„ÅÑ„Åæ„Åô..üêï</p>
      </div>
    );
  }

  const averageRating =
    store.reviews && store.reviews.length > 0
      ? store.reviews.reduce((sum, review) => sum + review.rating, 0) /
        store.reviews.length
      : 0;

  return (
    <div className="container">
      <h1 className="store-name">{store.store_name}</h1>
      <img src={store.store_img} alt={store.store_name} className="store-image" />

      <button
        onClick={handleFavoriteClick}
        className={`favorite-button ${isFavorite ? "active" : "inactive"}`}
      >
        {isFavorite ? "„ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§" : "„ÅäÊ∞ó„Å´ÂÖ•„Çä"}
      </button>

      <div className="rating-container">
        <div>‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ</div>
        <div
          className="filled-stars"
          style={{ width: `${(averageRating / 5) * 100}%` }}
        >
          ‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ
        </div>
      </div>
      <p>{averageRating.toFixed(1)}</p>

      <p>
        <strong>‰ΩèÊâÄ: </strong>
        {store.store_address}
      </p>

      <iframe
        title="Google Map"
        width="100%"
        height="300"
        src={`https://www.google.com/maps/embed/v1/place?key=${MAP_API_KEY}&q=${encodeURIComponent(
          store.store_address
        )}`}
        allowFullScreen
      ></iframe>

      <p>ÈõªË©±Áï™Âè∑: {store.store_phone_number}</p>
      <p>Âñ∂Ê•≠ÊôÇÈñì: {store.store_opening_hours}</p>

      {store.reviews && store.reviews.length > 0 ? (
        store.reviews.map((review) => (
          <div key={review.id} className="review">
            <p>Ë©ï‰æ°: {review.rating} / 5</p>
            <p>{review.comment}</p>
          </div>
        ))
      ) : (
        <p>„Åæ„Å†Âè£„Ç≥„Éü„ÅØ„ÅÇ„Çä„Åæ„Åõ„Çì</p>
      )}

      {store.reviews && store.reviews.length > 0 && (
        <Link to={`/dogrun/reviews/${store.store_id}`} className="link">
          Âè£„Ç≥„Éü„ÇíË¶ã„Çã
        </Link>
      )}

      <a
        href={store.store_url}
        target="_blank"
        rel="noopener noreferrer"
        className="link"
      >
        Â∫óËàó„ÅÆÂÖ¨Âºè„Çµ„Ç§„Éà
      </a>
    </div>
  );
};

export default DogRunDetail;
