import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./DogRunDetail.css";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: string;
  store_phone_number: string;
  store_url: string;
  store_img: string;
  tags: string[];
  reviews?: Review[];
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}

const DogRunDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [store, setStore] = useState<Store | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isFavorite, setIsFavorite] = useState<boolean>(false);

  const userId = 1; // ‰ªÆ„ÅÆ„É¶„Éº„Ç∂„ÉºID

  // „Éá„Éº„ÇøÂèñÂæóÔºàÂ∫óËàóÊÉÖÂ†±„ÄÅ„É¨„Éì„É•„Éº„ÄÅ„ÅäÊ∞ó„Å´ÂÖ•„ÇäÁä∂ÊÖãÔºâ
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [storeResponse, reviewResponse, favoriteResponse] = await Promise.all([
          fetch(`http://localhost:5003/stores/detail/${id}`),
          fetch(`http://localhost:5003/reviews`),
          fetch(`http://localhost:5000/favorites/${userId}`),
        ]);

        if (!storeResponse.ok || !reviewResponse.ok || !favoriteResponse.ok) {
          throw new Error("„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
        }

        const storeData: Store = await storeResponse.json();
        const reviewData: Review[] = await reviewResponse.json();
        const favoriteData = await favoriteResponse.json();

        // Â∫óËàó„Å´Èñ¢ÈÄ£„Åô„ÇãÂè£„Ç≥„Éü„Éá„Éº„Çø„ÇíÁµê„Å≥‰ªò„Åë
        const reviews = reviewData.filter((review) => review.store_id === storeData.store_id);
        setStore({ ...storeData, reviews });

        // „ÅäÊ∞ó„Å´ÂÖ•„ÇäÁä∂ÊÖã„ÇíÁ¢∫Ë™ç
        const isAlreadyFavorite = favoriteData.some(
          (fav: { store_id: number }) => fav.store_id === Number(id)
        );
        setIsFavorite(isAlreadyFavorite);
      } catch (err: any) {
        console.error(err.message);
        setError("„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ");
      }
    };

    fetchData();
  }, [id]);

  // „ÅäÊ∞ó„Å´ÂÖ•„ÇäËøΩÂä†„ÉªËß£Èô§
  const handleFavoriteClick = async () => {
    try {
      const method = isFavorite ? "DELETE" : "POST";
      const response = await fetch("http://localhost:5000/favorites", {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_id: userId, store_id: store?.store_id }),
      });

      if (!response.ok) throw new Error("„ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");

      setIsFavorite(!isFavorite);
      alert(isFavorite ? "„ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§„Åó„Åæ„Åó„Åü" : "„ÅäÊ∞ó„Å´ÂÖ•„Çä„Å´ËøΩÂä†„Åó„Åæ„Åó„Åü");
    } catch (error) {
      console.error("„ÅäÊ∞ó„Å´ÂÖ•„ÇäÊõ¥Êñ∞„Ç®„É©„Éº:", error);
    }
  };

  if (error) return <div className="error">{error}</div>;
  if (!store) return <div>„Éá„Éº„Çø„ÇíË™≠„ÅøËæº„Çì„Åß„ÅÑ„Åæ„Åô..üêï</div>;

  return (
    <div className="container">
      <header className="App-header">
        <h1>Wan Paradise</h1>
      </header>
      <h1>{store.store_name}</h1>

      {/* „ÅäÊ∞ó„Å´ÂÖ•„Çä„Éú„Çø„É≥ */}
      <button
        className={`favorite-button ${isFavorite ? "active" : ""}`}
        onClick={handleFavoriteClick}
      >
        {isFavorite ? "„ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§" : "„ÅäÊ∞ó„Å´ÂÖ•„ÇäËøΩÂä†"}
      </button>

      <img src={store.store_img} alt={store.store_name} />
      <p><strong>‰ΩèÊâÄ: </strong>{store.store_address}</p>
      <p><strong>ÈõªË©±Áï™Âè∑: </strong>{store.store_phone_number}</p>
      <p><strong>Âñ∂Ê•≠ÊôÇÈñì: </strong>{store.store_opening_hours}</p>

      {/* Google Map */}
      <iframe
        title="Google Map"
        width="100%"
        height="300"
        style={{ border: "0", borderRadius: "8px" }}
        src={`https://www.google.com/maps/embed/v1/place?key=YOUR_GOOGLE_API_KEY&q=${encodeURIComponent(store.store_address)}`}
        allowFullScreen
      ></iframe>

      {/* Âè£„Ç≥„ÉüË°®Á§∫ */}
      {store.reviews && store.reviews.length > 0 ? (
        store.reviews.map((review) => (
          <div key={review.id}>
            <p>‚≠ê{review.rating}</p>
            <p>{review.comment}</p>
          </div>
        ))
      ) : (
        <p>„Åæ„Å†Âè£„Ç≥„Éü„ÅØ„ÅÇ„Çä„Åæ„Åõ„Çì</p>
      )}

      {/* Âè£„Ç≥„Éü„Éö„Éº„Ç∏„Å∏„ÅÆ„É™„É≥„ÇØ */}
      {store.reviews && store.reviews.length > 0 && (
        <Link to={`/dogrun/reviews/${store.store_id}`} className="link">
          Âè£„Ç≥„Éü„ÇíË¶ã„Çã
        </Link>
      )}

      {/* ÂÖ¨Âºè„Çµ„Ç§„Éà */}
      <br />
      <a href={store.store_url} target="_blank" rel="noopener noreferrer">
        Â∫óËàó„ÅÆÂÖ¨Âºè„Çµ„Ç§„Éà
      </a>
    </div>
  );
};

export default DogRunDetail;
