import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./DogRunDetail.css";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: string;
  store_phone_number: string;
  store_url: string;
  store_img: string;
  tags: string[]; // „Çø„Ç∞„ÅÆÈÖçÂàó„Å®„Åó„Å¶ÂÆöÁæ©
  reviews?: Review[];
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}

const DogRunDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [store, setStore] = useState<Store | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [selectedRating, setSelectedRating] = useState<number>(0);
  const [averageRating, setAverageRating] = useState<number>(0);
  //„ÅäÊ∞ó„Å´ÂÖ•„ÇäÁä∂ÊÖã„ÇíÁÆ°ÁêÜ
  const [isFavorite, setIsFavorite] = useState<boolean>(false);

  const userId = 1; //‰ªÆ„ÅÆ„É¶„Éº„Ç∂„ÉºID

  // „ÅäÊ∞ó„Å´ÂÖ•„ÇäÁä∂ÊÖã„ÇíÂèñÂæó
  useEffect(() => {
    const fetchFavoriteStatus = async () => {
      try {
        const response = await fetch(`http://localhost:5000/favorites/${userId}`);
        if (!response.ok) throw new Error("„ÅäÊ∞ó„Å´ÂÖ•„Çä„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");

        const favorites = await response.json();
        const isAlreadyFavorite = favorites.some(
          (fav: { store_id: number }) => fav.store_id === Number(id)
        );
        setIsFavorite(isAlreadyFavorite);
      } catch (error) {
        console.error("„ÅäÊ∞ó„Å´ÂÖ•„ÇäÂèñÂæó„Ç®„É©„Éº:", error);
      }
    };

    fetchFavoriteStatus();
  }, [id]);

  // Â∫óËàóÊÉÖÂ†±„ÅÆÂèñÂæó
  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await fetch(
          `http://localhost:5003/stores/detail/${id}`
        );
        if (!response.ok) {
          throw new Error(`„Çµ„Éº„Éê„Éº„Ç®„É©„Éº: ${response.status}`);
        }
        const data = await response.json();
        console.log(data);

        setStore(data);
      } catch (error) {
        console.error("Â∫óËàóÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:", error);
      }
    };
    fetchStores();
  }, [id]);



  // „ÅäÊ∞ó„Å´ÂÖ•„ÇäËøΩÂä†„ÉªËß£Èô§
  const handleFavoriteClick = async () => {
    try {
      const method = isFavorite ? "DELETE" : "POST";
      const response = await fetch("http://localhost:5000/favorites", {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_id: userId, store_id: store?.store_id }),
      });

      if (!response.ok) throw new Error("„ÅäÊ∞ó„Å´ÂÖ•„ÇäÁä∂ÊÖã„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");

      setIsFavorite(!isFavorite);
      alert(isFavorite ? "„ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§„Åó„Åæ„Åó„Åü" : "„ÅäÊ∞ó„Å´ÂÖ•„Çä„Å´ËøΩÂä†„Åó„Åæ„Åó„Åü");
    } catch (error) {
      console.error("„ÅäÊ∞ó„Å´ÂÖ•„ÇäÊõ¥Êñ∞„Ç®„É©„Éº:", error);
    }
  };

  if (error) return <div>„Ç®„É©„Éº: {error}</div>;
  if (!store) return <div>„Éá„Éº„Çø„ÇíË™≠„ÅøËæº„Çì„Åß„ÅÑ„Åæ„Åô..üêï</div>;


  //MAP
  const MAP_API_KEY = "AIzaSyBamO2vaf6PMNHy3bhMJd_2FO2I6IUgEpE";


  const handleStarClick = (value: number) => {
    if (selectedRating === value) {
      setSelectedRating(0);
    } else {
      setSelectedRating(value);
    }
  };




  // Â∫óËàó„Éá„Éº„Çø„Å®„É¨„Éì„É•„Éº„ÇíÂèñÂæó„Åó„Å¶Ë®≠ÂÆö„Åô„ÇãÈñ¢Êï∞

  useEffect(() => {
    const fetchStoreWithReviews = async () => {
      try {
        const storeResponse = await fetch(`http://localhost:5003/stores/detail/${id}`);
        const reviewResponse = await fetch(`http://localhost:5003/reviews`);

        if (!storeResponse.ok || !reviewResponse.ok) {
          throw new Error("„Éá„Éº„ÇøÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
        }

        const storeData: Store = await storeResponse.json();
        const reviewData: Review[] = await reviewResponse.json();

        // Â∫óËàó„Å´Èñ¢ÈÄ£‰ªò„Åë„Çâ„Çå„ÅüÂè£„Ç≥„Éü„ÇíÁµê„Å≥‰ªò„Åë„Çã
        const reviews = reviewData.filter((review) => review.store_id === storeData.store_id);
        setStore({ ...storeData, reviews });
      } catch (err: any) {
        console.error("„Éá„Éº„ÇøÂèñÂæó‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü:", err);
        setError("Â∫óËàóÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
      }
    };

    fetchStoreWithReviews();
  }, [id]);
   // „Åì„Åì„Åã„ÇâÊù°‰ª∂‰ªò„Åç„ÅÆ„É™„Çø„Éº„É≥Âá¶ÁêÜ
  if (error) {
   return (
    <div style={{ padding: "20px", backgroundColor: "#FAF3E0", textAlign: "center" }}>
      <p>{error}</p>
    </div>
    );
   }

  if (!store) {
   return (
    <div>
      <p>„Éá„Éº„Çø„ÇíË™≠„ÅøËæº„Çì„Åß„ÅÑ„Åæ„Åô..üêï</p>
    </div>
   );
  }



  return (
    <div className="container">
      <header className="App-header">
        <h1>Wan Paradise</h1>
      </header>
      <h1>{store.store_name}</h1>
      {/*„ÅäÊ∞ó„Å´ÂÖ•„Çä„Éú„Çø„É≥*/}
      <button className={`favorite-button ${isFavorite ? "active" : ""}`} onClick={handleFavoriteClick}>
        {isFavorite ? "„ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§" : "„ÅäÊ∞ó„Å´ÂÖ•„ÇäËøΩÂä†"}
      </button>

      <div className="average-rating">
        {[1, 2, 3, 4, 5].map((value) => (
          <span
            key={value}
            className={`star ${value <= Math.round(averageRating) ? "selected" : ""}`}
          >
            ‚òÖ
          </span>
        ))}
      </div>
      <span className="average-rating-value">
        {averageRating.toFixed(1)}
      </span>
      <img src={store.store_img} alt={store.store_name} />
      <p className="store-title">Ëá™ÁÑ∂„Å´Âõ≤„Åæ„Çå„Åü2000Âù™„ÅÆ„Éâ„ÉÉ„Ç∞„É©„É≥„Åß„ÄÅÂ§ßÂûãÁä¨„Åß„ÇÇÊ•Ω„Åó„ÇÅ„Åæ„Åô</p>
      <p>
        <strong>‰ΩèÊâÄ: </strong>
        {store.store_address}
      </p>

      <p>ÈõªË©±Áï™Âè∑: {store.store_phone_number}</p>
      <p>Âñ∂Ê•≠ÊôÇÈñì: {store.store_opening_hours}</p>
      {/*/Google map Âüã„ÇÅËæº„Åø*/}
      <iframe
        title="Google Map"
        width="100%"
        height="300"
        style={{ border: "0", borderRadius: "8px" }}
        src={`https://www.google.com/maps/embed/v1/place?key=${MAP_API_KEY}&q=${encodeURIComponent(
          store.store_address
        )}`}
        allowFullScreen
      ></iframe>
      {store.reviews && store.reviews.length > 0 ? (
        store.reviews.map((review) => (
          <div key={review.id}>

          </div>
        ))
      ) : (
